INSERT INTO `visua_sql_example` VALUES ('4e54c4979bfc4f23a52929a6d759718a', '查询京东员工管理注册表', 'S08017', '柳瑞川', '1', '3', '0', '2018-09-04 00:24:11', '2018-09-14 15:32:28', '[{\"checkType\":[],\"name\":\"userAcct\",\"value\":\"员工编号\"},{\"checkType\":[],\"name\":\"userName\",\"value\":\"员工姓名\"},{\"checkType\":[],\"name\":\"timet\",\"value\":\"时间\"},{\"checkType\":[],\"name\":\"path\",\"value\":\"照片路径\"},{\"checkType\":[],\"name\":\"type\",\"value\":\"照片类型\"},{\"checkType\":[],\"name\":\"checkRet\",\"value\":\"图片审核状态\"},{\"checkType\":[],\"name\":\"name\",\"value\":\"文件名\"},{\"checkType\":[],\"name\":\"id\",\"value\":\"文件id\"},{\"checkType\":[],\"name\":\"gender\",\"value\":\"性别\"},{\"checkType\":[],\"name\":\"dept\",\"value\":\"部门\"},{\"checkType\":[],\"name\":\"phone\",\"value\":\"手机号\"},{\"checkType\":[],\"name\":\"prio\",\"value\":\"优先级\"},{\"checkType\":[],\"name\":\"status\",\"value\":\"照片审核状态\"},{\"checkType\":[],\"name\":\"state\",\"value\":\"照片状态\"},{\"checkType\":[],\"name\":\"describe\",\"value\":\"照片审核描述\"}]', '[{\"isSkip\":true,\"name\":\"storeID\",\"testVal\":\"9b200cfeaaa611e882d1fa163ec0ad5d\",\"value\":\"门店ID\",\"verifyTypeStr\":[],\"verifyType\":{}},{\"isSkip\":false,\"name\":\"userID\",\"value\":\"员工ID\",\"verifyTypeStr\":[],\"verifyType\":{}}]', '[{\"isReturn\":1,\"name\":\"datalist\",\"type\":\"sel\",\"value\":\" SELECT userAcct,userName,DATE_FORMAT(timet,\'%Y-%m-%d %H:%i:%s\') as timet,storeID,a.userID,IF(SUM(IF(b.checkRet!=1,1,0))>0, IF(SUM(IF(b.type=1,1,0))=0, IF(SUM(IF(b.type=0&&b.checkRet=2,1,0))=1,2,3), IF(SUM(IF(b.type=0&&b.checkRet=2,1,0))=0,3,IF(SUM(IF(b.type=1&&b.checkRet=2,1,0))>0,2,IF(SUM(IF(b.type=1&&b.checkRet>2,1,0))=(COUNT(b.checkRet)-1),3,2)))),1 )as \'checkRet\', \\na.prio,a.gender,a.dept,a.phone  FROM jd_usert a\\nLEFT JOIN jd_userphotot b ON a.`userID` =b.`userID`\\n<#if storeID??>\\nWHERE a.storeID =\'${storeID}\'\\n</#if>\\nGROUP BY userAcct,userName,timet,storeID,a.userID,a.prio,a.gender,a.dept,a.phone \\nORDER BY timet desc\",\